    protected void ImageCheckChanged(MouseEventArgs e, GearImage image)
    {
        var result = Images;

        if (!image.IsUsed)
        {
            if (Lens.Images == null)
            {
                Lens.Images = new List<GearImage>();
            }
            Lens.Images.Add(image);
        }
    }

    protected async override Task OnParametersSetAsync()
    {
        if (Id != 0)
